/**
 * Call the function before property is deserialization.
 *
 * ## Example
 *
 * ```typescript
 * class Model {
 *    @PropertyDeserialize(v => v + 1)
 *    property: string;
 * }
 * ```
 *
 * @param {Function} fn
 * @returns {Function}
 * @decorator
 * @converters
 * @jsonschema
 * @property
 */
export declare function PropertyDeserialize(fn: (value: any) => any): Function;
